<?php/** * @file views-view-unformatted.tpl.php * Default simple view template to display a list of rows. * * @ingroup views_templates */ $first = TRUE; ?><?php if (!empty($title)): ?>  <h3><?php print $title; ?></h3><?php endif; ?>  <div id="myCarousel" class="carousel slide">    <div class="carousel-inner"><?php foreach ($rows as $id => $row): ?>  <div class="<?php print $classes_array[$id]; ?> <?php print ($first ? 'active' : '') ?>">    <?php       print $row;       $first = FALSE;    ?>  </div><?php endforeach; ?>    </div>    <a class="carousel-control left" href="#myCarousel" data-slide="prev">&lsaquo;</a>    <a class="carousel-control right" href="#myCarousel" data-slide="next">&rsaquo;</a>      </div>